import React, { PureComponent } from 'react';
import {Container} from "react-bootstrap";



const Question = e => {
  const { no, prob_name } = e;
  let a = e.no ? e.no : 69;
  let b = prob_name === "" ? prob_name : "Placeholder Question Title";
  return (
    <div
      style={{
        paddingTop: "1.5em"
      }}
    >
      <Container
        style={{
          marginLeft: "10px",

          boxShadow: "-1px 0px 2px #d4d2d2",
          paddingLeft: "0px",
          width: "70%",
          paddingRight: "0px",
          marginTop: "5px",
          display: "flex",
          flexDirection: "horizontal",
          backgroundColor: "#ffffff",
          height: "7rem",
          justifyContent: "true",
          alignItems: "center",
          fontSize: "2.4vw"
        }}
      >
        <div
          style={{
            padding: "10px",
            marginLeft: "0px"
          }}
        >
          <h3
            style={{
              padding: "5px",

              borderBottom: "2px solid #dee0df"
            }}
          >
            # Assignment {a}{" "}
          </h3>
          <div>{b}</div>
        </div>


      </Container>
    </div>
  );
};

export default Question
