import React, { Component } from 'react'
import {Container} from 'react-bootstrap';
import { FaRegStar } from 'react-icons/fa';
import { FaStar} from 'react-icons/fa';





const stars =(no) => {
    let i =0;
    var stararray=[];

    for (  i =0 ; i <  no ; i++){
        stararray.push(<FaStar/>)
    }
    for ( ; i < 5 ; i++){
        stararray.push(<FaRegStar/>)
    }
    return stararray;
};

const Assg = (e) => {
    const { no , prob_name} = e;
    let a = e.no ? e.no : 69;
    let b = prob_name === "" ? prob_name : "Placeholder Question Title";
    return (
      <Container
        style={{
          marginLeft: "10px",

          boxShadow: "-1px 0px 2px #000000",
          paddingLeft: "0px",
          width: "70%",
          paddingRight: "0px",
          marginTop: "5px",
          display: "flex",
          flexDirection: "horizontal",
          backgroundColor: "#ffffff",
          height: "7rem",
          justifyContent:"true",
          alignItems:"center"
        }}
      >
        <div
          style={{
            padding: "10px",
            marginLeft: "0px"
          }}
        >
          <h2
            style={{
              padding: "5px",

              borderBottom: "2px solid #dee0df"
            }}
          >
            # Assignment {a}{" "}
          </h2>
          <h4>{b}</h4>
        </div>

        <div
          style={{
            backgroundColor: "#ffffff"
          }}
        >
          <div
            style={{
              position: "relative",
              left:"3em",
              width:"100%"

            }}
          >
            <label><strong>Difficulty</strong></label>{stars(2)}

          </div>
        </div>
      </Container>
    );
}

export default Assg;
